    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        String jtMarca = jTextField1.getText();
        String jtModelo = jTextField2.getText();
        int jtTamanho = Integer.parseInt(jTextField3.getText());
        double jtPreco = Double.parseDouble(jTextField4.getText());

        Chuteira chuteira1 = new Chuteira();
        chuteira1.setMarca(jtMarca);
        chuteira1.setModelo(jtModelo);
        chuteira1.setTamanho(jtTamanho);
        chuteira1.setPreco(jtPreco);

        BD acessoUm = new BD();
        
        
        acessoUm.Inserir(chuteira1.getMarca(),chuteira1.getModelo(),chuteira1.getTamanho(),chuteira1.getPreco());
        
    }//GEN-LAST:event_jButton1ActionPerformed

